var test = require('./tape')
var mongojs = require('../index')
var db = mongojs('test', ['test123'])

test('getCollection', function (t) {
  db.test123.drop(function () {
    db.getCollection('test123', function (err) {
      t.error(err)
      db.getCollection('test123', function (err) {
        t.ok(err)
        t.end()
      })
    })
  })
})
